#include <stdint.h>
#include <stdbool.h>
#include <string.h>

/* ARM Cortex-M specific optimizations and NASA compliance */
#define ARM_STACK_ALIGN 8U
#define ARM_STACK_SIZE 2048U
#define ARM_CACHE_LINE_SIZE 32U

/* ARM-specific memory barriers */
#define ARM_DSB() __asm volatile("dsb 0xf" : : : "memory")
#define ARM_ISB() __asm volatile("isb 0xf" : : : "memory")
#define ARM_DMB() __asm volatile("dmb 0xf" : : : "memory")

/* ARM Cortex-M system control block structure */
typedef struct {
    uint32_t cpuid;        /* CPU ID Base Register */
    uint32_t icsr;         /* Interrupt Control State Register */
    uint32_t vtor;         /* Vector Table Offset Register */
    uint32_t aircr;        /* Application Interrupt and Reset Control Register */
    uint32_t scr;          /* System Control Register */
    uint32_t ccr;          /* Configuration Control Register */
    uint32_t shpr1;        /* System Handler Priority Register 1 */
    uint32_t shpr2;        /* System Handler Priority Register 2 */
    uint32_t shpr3;        /* System Handler Priority Register 3 */
    uint32_t shcsr;        /* System Handler Control and State Register */
    uint32_t cfsr;         /* Configurable Fault Status Register */
    uint32_t hfsr;         /* Hard Fault Status Register */
    uint32_t dfsr;         /* Debug Fault Status Register */
    uint32_t mmfar;        /* MemManage Fault Address Register */
    uint32_t bfar;         /* Bus Fault Address Register */
} arm_scb_t;

/* ARM Cortex-M NVIC structure */
typedef struct {
    uint32_t iser[8];      /* Interrupt Set-Enable Registers */
    uint32_t icer[8];      /* Interrupt Clear-Enable Registers */
    uint32_t ispr[8];      /* Interrupt Set-Pending Registers */
    uint32_t icpr[8];      /* Interrupt Clear-Pending Registers */
    uint32_t iabr[8];      /* Interrupt Active Bit Registers */
    uint8_t ipr[240];      /* Interrupt Priority Registers */
} arm_nvic_t;

/* ARM Cortex-M SysTick structure */
typedef struct {
    uint32_t ctrl;         /* SysTick Control and Status Register */
    uint32_t reload;       /* SysTick Reload Value Register */
    uint32_t current;      /* SysTick Current Value Register */
    uint32_t calib;        /* SysTick Calibration Value Register */
} arm_systick_t;

/* ARM Cortex-M specific sensor data structure */
typedef struct {
    uint16_t sensor_id;
    uint32_t timestamp;    /* ARM SysTick timestamp */
    int16_t temperature;   /* Temperature in 0.1째C units */
    uint16_t pressure;     /* Pressure in 0.1 mbar units */
    uint8_t humidity;      /* Humidity in 0.1% units */
    bool is_valid;
    uint8_t padding[2];    /* Padding for ARM alignment */
} arm_sensor_data_t;

/* ARM Cortex-M sensor array with bounds checking */
typedef struct {
    arm_sensor_data_t sensors[16U];  /* Fixed size array - NASA Rule 3 */
    uint8_t active_count;
    uint32_t system_uptime;
    bool system_healthy;
    uint8_t padding[3];    /* ARM alignment padding */
} arm_sensor_system_t;

/* ARM Cortex-M stack with overflow protection */
typedef struct {
    uint8_t data[ARM_STACK_SIZE];
    uint32_t magic_start;  /* Magic number for overflow detection */
    uint32_t magic_end;    /* Magic number for overflow detection */
} arm_stack_t;

/* Magic numbers for ARM stack overflow detection */
#define ARM_STACK_MAGIC_START 0xDEADBEEFU
#define ARM_STACK_MAGIC_END 0xCAFEBABEU

/* ARM Cortex-M static memory pools */
#define ARM_POOL_COUNT 3U
#define ARM_POOL_0_SIZE 32U
#define ARM_POOL_1_SIZE 64U
#define ARM_POOL_2_SIZE 128U

/* ARM memory pool structure */
typedef struct {
    uint8_t* data;
    uint16_t block_size;
    uint16_t block_count;
    bool* allocated;
    uint16_t free_count;
    uint8_t padding[2];    /* ARM alignment padding */
} arm_memory_pool_t;

/* ARM static memory pool data */
static uint8_t arm_pool_0_data[ARM_POOL_0_SIZE * 8U] __attribute__((aligned(ARM_CACHE_LINE_SIZE)));
static uint8_t arm_pool_1_data[ARM_POOL_1_SIZE * 4U] __attribute__((aligned(ARM_CACHE_LINE_SIZE)));
static uint8_t arm_pool_2_data[ARM_POOL_2_SIZE * 2U] __attribute__((aligned(ARM_CACHE_LINE_SIZE)));

static bool arm_pool_0_allocated[8U];
static bool arm_pool_1_allocated[4U];
static bool arm_pool_2_allocated[2U];

static arm_memory_pool_t arm_memory_pools[ARM_POOL_COUNT];

/* ARM Cortex-M system instances */
static arm_sensor_system_t arm_sensor_system;
static arm_stack_t arm_main_stack __attribute__((aligned(ARM_STACK_ALIGN)));

/* ARM Cortex-M specific initialization */
void arm_initialize_system(void) {
    /* Initialize ARM stack with overflow protection */
    arm_main_stack.magic_start = ARM_STACK_MAGIC_START;
    arm_main_stack.magic_end = ARM_STACK_MAGIC_END;

    /* Initialize ARM sensor system */
    memset(&arm_sensor_system, 0, sizeof(arm_sensor_system_t));
    arm_sensor_system.system_healthy = true;

    /* Initialize ARM memory pools */
    arm_memory_pools[0].data = arm_pool_0_data;
    arm_memory_pools[0].block_size = ARM_POOL_0_SIZE;
    arm_memory_pools[0].block_count = 8U;
    arm_memory_pools[0].allocated = arm_pool_0_allocated;
    arm_memory_pools[0].free_count = 8U;

    arm_memory_pools[1].data = arm_pool_1_data;
    arm_memory_pools[1].block_size = ARM_POOL_1_SIZE;
    arm_memory_pools[1].block_count = 4U;
    arm_memory_pools[1].allocated = arm_pool_1_allocated;
    arm_memory_pools[1].free_count = 4U;

    arm_memory_pools[2].data = arm_pool_2_data;
    arm_memory_pools[2].block_size = ARM_POOL_2_SIZE;
    arm_memory_pools[2].block_count = 2U;
    arm_memory_pools[2].allocated = arm_pool_2_allocated;
    arm_memory_pools[2].free_count = 2U;

    /* Initialize allocation flags */
    for (uint16_t i = 0; i < 8U; i++) arm_pool_0_allocated[i] = false;
    for (uint16_t i = 0; i < 4U; i++) arm_pool_1_allocated[i] = false;
    for (uint16_t i = 0; i < 2U; i++) arm_pool_2_allocated[i] = false;

    /* ARM memory barriers for initialization */
    ARM_DMB();
    ARM_DSB();
}

/* ARM Cortex-M stack overflow detection */
bool arm_check_stack_overflow(void) {
    return (arm_main_stack.magic_start != ARM_STACK_MAGIC_START) ||
           (arm_main_stack.magic_end != ARM_STACK_MAGIC_END);
}

/* ARM Cortex-M memory pool allocation */
void* arm_allocate_from_pool(uint16_t size) {
    for (uint16_t i = 0; i < ARM_POOL_COUNT; i++) {
        if (size <= arm_memory_pools[i].block_size && 
            arm_memory_pools[i].free_count > 0) {

            arm_memory_pool_t* pool = &arm_memory_pools[i];

            /* Find free block */
            for (uint16_t j = 0; j < pool->block_count; j++) {
                if (!pool->allocated[j]) {
                    pool->allocated[j] = true;
                    pool->free_count--;

                    /* ARM cache operations */
                    ARM_DMB();

                    return pool->data + (j * pool->block_size);
                }
            }
        }
    }

    return NULL;  /* No suitable pool */
}

/* ARM Cortex-M memory pool release */
bool arm_release_to_pool(void* ptr) {
    if (ptr == NULL) {
        return false;
    }

    /* Find which pool this pointer belongs to */
    for (uint16_t i = 0; i < ARM_POOL_COUNT; i++) {
        arm_memory_pool_t* pool = &arm_memory_pools[i];

        if (ptr >= pool->data && 
            ptr < pool->data + (pool->block_count * pool->block_size)) {

            /* Calculate block index */
            uint16_t block_index = ((uint8_t*)ptr - pool->data) / pool->block_size;

            if (block_index < pool->block_count && pool->allocated[block_index]) {
                pool->allocated[block_index] = false;
                pool->free_count++;

                /* ARM memory barriers */
                ARM_DMB();

                return true;
            }
        }
    }

    return false;
}

/* ARM Cortex-M sensor data validation */
bool arm_validate_sensor_data(const arm_sensor_data_t* sensor) {
    if (sensor == NULL) {
        return false;
    }

    /* Validate sensor ID range */
    if (sensor->sensor_id >= 16U) {
        return false;
    }

    /* Validate temperature range (-50째C to +150째C) */
    if (sensor->temperature < -500 || sensor->temperature > 1500) {
        return false;
    }

    /* Validate pressure range (800-1200 mbar) */
    if (sensor->pressure < 8000 || sensor->pressure > 12000) {
        return false;
    }

    /* Validate humidity range (0-100%) */
    if (sensor->humidity > 1000) {
        return false;
    }

    return true;
}

/* ARM Cortex-M sensor data processing with bounds checking */
bool arm_process_sensor_data(uint8_t sensor_index, const arm_sensor_data_t* new_data) {
    if (sensor_index >= 16U || new_data == NULL) {
        return false;
    }

    if (!arm_validate_sensor_data(new_data)) {
        return false;
    }

    /* Update sensor data with ARM-specific optimizations */
    arm_sensor_system.sensors[sensor_index] = *new_data;
    arm_sensor_system.sensors[sensor_index].timestamp = arm_sensor_system.system_uptime;

    /* ARM memory barriers for data consistency */
    ARM_DMB();

    /* Update system health based on sensor validity */
    if (new_data->is_valid) {
        arm_sensor_system.active_count++;
    } else {
        arm_sensor_system.active_count--;
    }

    /* Check system health threshold */
    if (arm_sensor_system.active_count < 8U) {
        arm_sensor_system.system_healthy = false;
    } else {
        arm_sensor_system.system_healthy = true;
    }

    return true;
}

/* ARM Cortex-M sensor array processing with bounded loops */
void arm_process_all_sensors(void) {
    uint8_t processed_count = 0U;
    uint8_t index = 0U;

    /* Bounded loop with explicit counter - NASA Rule 2 */
    while (index < 16U && processed_count < 16U) {
        if (arm_sensor_system.sensors[index].is_valid) {
            /* Process valid sensor data */
            arm_sensor_data_t* sensor = &arm_sensor_system.sensors[index];

            /* Check for critical sensor readings */
            if (sensor->temperature > 1000) {  /* > 100째C */
                /* Handle high temperature condition */
                handle_high_temperature(sensor->sensor_id, sensor->temperature);
            }

            if (sensor->pressure < 9000 || sensor->pressure > 11000) {
                /* Handle pressure anomaly */
                handle_pressure_anomaly(sensor->sensor_id, sensor->pressure);
            }

            processed_count++;
        }

        index++;
    }

    /* Update system uptime */
    arm_sensor_system.system_uptime++;
}

/* ARM Cortex-M interrupt-safe operations */
void arm_interrupt_safe_operation(void) {
    /* Disable interrupts for critical section */
    uint32_t primask = __get_PRIMASK();
    __disable_irq();

    /* Critical section - update shared data */
    arm_sensor_system.system_uptime++;

    /* ARM memory barriers for interrupt safety */
    ARM_DMB();
    ARM_DSB();

    /* Restore interrupt state */
    if (!primask) {
        __enable_irq();
    }
}

/* ARM Cortex-M cache operations */
void arm_cache_operations(void) {
    /* Clean cache line */
    for (uint32_t addr = (uint32_t)&arm_sensor_system; 
         addr < (uint32_t)&arm_sensor_system + sizeof(arm_sensor_system_t); 
         addr += ARM_CACHE_LINE_SIZE) {

        /* ARM Cortex-M cache clean operation */
        __asm volatile("dc civac, %0" : : "r"(addr) : "memory");
    }

    /* Data synchronization barrier */
    ARM_DSB();

    /* Instruction synchronization barrier */
    ARM_ISB();
}

/* ARM Cortex-M system health monitoring */
bool arm_check_system_health(void) {
    /* Check stack overflow */
    if (arm_check_stack_overflow()) {
        return false;
    }

    /* Check sensor system health */
    if (!arm_sensor_system.system_healthy) {
        return false;
    }

    /* Check memory pool health */
    for (uint16_t i = 0; i < ARM_POOL_COUNT; i++) {
        if (arm_memory_pools[i].free_count == 0) {
            return false;  /* All pools exhausted */
        }
    }

    return true;
}

/* ARM Cortex-M main function with NASA compliance */
int main(void) {
    /* Initialize ARM Cortex-M system */
    arm_initialize_system();

    /* Check initial system health */
    if (!arm_check_system_health()) {
        return -1;  /* System initialization failed */
    }

    /* Main system loop with bounded execution */
    uint32_t loop_count = 0U;
    const uint32_t MAX_LOOPS = 1000000U;  /* NASA Rule 2: Fixed upper bound */

    while (loop_count < MAX_LOOPS) {
        /* Process all sensors */
        arm_process_all_sensors();

        /* Check system health periodically */
        if (loop_count % 1000U == 0) {
            if (!arm_check_system_health()) {
                break;  /* Exit on health failure */
            }
        }

        /* Interrupt-safe operations */
        arm_interrupt_safe_operation();

        /* Cache operations */
        arm_cache_operations();

        loop_count++;
    }

    return (int)loop_count;
}
