#include <stdint.h>
#include <stdbool.h>
#include <string.h>

/* PowerPC e500 specific optimizations and NASA compliance */
#define PPC_STACK_ALIGN 16U
#define PPC_STACK_SIZE 4096U
#define PPC_CACHE_LINE_SIZE 64U

/* PowerPC-specific memory barriers */
#define PPC_SYNC() __asm volatile("sync" : : : "memory")
#define PPC_ISYNC() __asm volatile("isync" : : : "memory")
#define PPC_EIEIO() __asm volatile("eieio" : : : "memory")

/* PowerPC e500 specific registers */
typedef struct {
    uint32_t msr;          /* Machine State Register */
    uint32_t ctr;          /* Count Register */
    uint32_t lr;           /* Link Register */
    uint32_t xer;          /* Fixed-Point Exception Register */
    uint32_t sprg0;        /* Special Purpose Register 0 */
    uint32_t sprg1;        /* Special Purpose Register 1 */
    uint32_t sprg2;        /* Special Purpose Register 2 */
    uint32_t sprg3;        /* Special Purpose Register 3 */
} ppc_registers_t;

/* PowerPC e500 cache control structure */
typedef struct {
    uint32_t l1csr0;       /* L1 Cache Control and Status Register 0 */
    uint32_t l1csr1;       /* L1 Cache Control and Status Register 1 */
    uint32_t l1csr2;       /* L1 Cache Control and Status Register 2 */
    uint32_t l1csr3;       /* L1 Cache Control and Status Register 3 */
} ppc_cache_control_t;

/* PowerPC e500 TLB structure */
typedef struct {
    uint32_t mas0;         /* MMU Assist Register 0 */
    uint32_t mas1;         /* MMU Assist Register 1 */
    uint32_t mas2;         /* MMU Assist Register 2 */
    uint32_t mas3;         /* MMU Assist Register 3 */
    uint32_t mas4;         /* MMU Assist Register 4 */
    uint32_t mas5;         /* MMU Assist Register 5 */
    uint32_t mas6;         /* MMU Assist Register 6 */
    uint32_t mas7;         /* MMU Assist Register 7 */
} ppc_tlb_t;

/* PowerPC e500 sensor data structure with big-endian consideration */
typedef struct {
    uint16_t sensor_id;    /* Big-endian format */
    uint32_t timestamp;    /* PowerPC timebase timestamp */
    int16_t temperature;   /* Temperature in 0.1째C units */
    uint16_t pressure;     /* Pressure in 0.1 mbar units */
    uint8_t humidity;      /* Humidity in 0.1% units */
    bool is_valid;
    uint8_t padding[2];    /* PowerPC alignment padding */
} ppc_sensor_data_t;

/* PowerPC e500 sensor array with bounds checking */
typedef struct {
    ppc_sensor_data_t sensors[32U];  /* Fixed size array - NASA Rule 3 */
    uint8_t active_count;
    uint32_t system_uptime;
    bool system_healthy;
    uint8_t padding[3];    /* PowerPC alignment padding */
} ppc_sensor_system_t;

/* PowerPC e500 stack with overflow protection */
typedef struct {
    uint8_t data[PPC_STACK_SIZE];
    uint32_t magic_start;  /* Magic number for overflow detection */
    uint32_t magic_end;    /* Magic number for overflow detection */
} ppc_stack_t;

/* Magic numbers for PowerPC stack overflow detection */
#define PPC_STACK_MAGIC_START 0xDEADBEEFU
#define PPC_STACK_MAGIC_END 0xCAFEBABEU

/* PowerPC e500 static memory pools with cache line alignment */
#define PPC_POOL_COUNT 4U
#define PPC_POOL_0_SIZE 64U
#define PPC_POOL_1_SIZE 128U
#define PPC_POOL_2_SIZE 256U
#define PPC_POOL_3_SIZE 512U

/* PowerPC memory pool structure */
typedef struct {
    uint8_t* data;
    uint16_t block_size;
    uint16_t block_count;
    bool* allocated;
    uint16_t free_count;
    uint8_t padding[2];    /* PowerPC alignment padding */
} ppc_memory_pool_t;

/* PowerPC static memory pool data with cache line alignment */
static uint8_t ppc_pool_0_data[PPC_POOL_0_SIZE * 16U] __attribute__((aligned(PPC_CACHE_LINE_SIZE)));
static uint8_t ppc_pool_1_data[PPC_POOL_1_SIZE * 8U] __attribute__((aligned(PPC_CACHE_LINE_SIZE)));
static uint8_t ppc_pool_2_data[PPC_POOL_2_SIZE * 4U] __attribute__((aligned(PPC_CACHE_LINE_SIZE)));
static uint8_t ppc_pool_3_data[PPC_POOL_3_SIZE * 2U] __attribute__((aligned(PPC_CACHE_LINE_SIZE)));

static bool ppc_pool_0_allocated[16U];
static bool ppc_pool_1_allocated[8U];
static bool ppc_pool_2_allocated[4U];
static bool ppc_pool_3_allocated[2U];

static ppc_memory_pool_t ppc_memory_pools[PPC_POOL_COUNT];

/* PowerPC e500 system instances */
static ppc_sensor_system_t ppc_sensor_system;
static ppc_stack_t ppc_main_stack __attribute__((aligned(PPC_STACK_ALIGN)));

/* PowerPC e500 endianness handling */
static inline uint16_t ppc_read_16_be(const uint8_t* data) {
    return (uint16_t)((data[0] << 8) | data[1]);
}

static inline uint32_t ppc_read_32_be(const uint8_t* data) {
    return (uint32_t)((data[0] << 24) | (data[1] << 16) | (data[2] << 8) | data[3]);
}

static inline void ppc_write_16_be(uint8_t* data, uint16_t value) {
    data[0] = (uint8_t)(value >> 8);
    data[1] = (uint8_t)(value & 0xFF);
}

static inline void ppc_write_32_be(uint8_t* data, uint32_t value) {
    data[0] = (uint8_t)(value >> 24);
    data[1] = (uint8_t)((value >> 16) & 0xFF);
    data[2] = (uint8_t)((value >> 8) & 0xFF);
    data[3] = (uint8_t)(value & 0xFF);
}

/* PowerPC e500 specific initialization */
void ppc_initialize_system(void) {
    /* Initialize PowerPC stack with overflow protection */
    ppc_main_stack.magic_start = PPC_STACK_MAGIC_START;
    ppc_main_stack.magic_end = PPC_STACK_MAGIC_END;

    /* Initialize PowerPC sensor system */
    memset(&ppc_sensor_system, 0, sizeof(ppc_sensor_system_t));
    ppc_sensor_system.system_healthy = true;

    /* Initialize PowerPC memory pools */
    ppc_memory_pools[0].data = ppc_pool_0_data;
    ppc_memory_pools[0].block_size = PPC_POOL_0_SIZE;
    ppc_memory_pools[0].block_count = 16U;
    ppc_memory_pools[0].allocated = ppc_pool_0_allocated;
    ppc_memory_pools[0].free_count = 16U;

    ppc_memory_pools[1].data = ppc_pool_1_data;
    ppc_memory_pools[1].block_size = PPC_POOL_1_SIZE;
    ppc_memory_pools[1].block_count = 8U;
    ppc_memory_pools[1].allocated = ppc_pool_1_allocated;
    ppc_memory_pools[1].free_count = 8U;

    ppc_memory_pools[2].data = ppc_pool_2_data;
    ppc_memory_pools[2].block_size = PPC_POOL_2_SIZE;
    ppc_memory_pools[2].block_count = 4U;
    ppc_memory_pools[2].allocated = ppc_pool_2_allocated;
    ppc_memory_pools[2].free_count = 4U;

    ppc_memory_pools[3].data = ppc_pool_3_data;
    ppc_memory_pools[3].block_size = PPC_POOL_3_SIZE;
    ppc_memory_pools[3].block_count = 2U;
    ppc_memory_pools[3].allocated = ppc_pool_3_allocated;
    ppc_memory_pools[3].free_count = 2U;

    /* Initialize allocation flags */
    for (uint16_t i = 0; i < 16U; i++) ppc_pool_0_allocated[i] = false;
    for (uint16_t i = 0; i < 8U; i++) ppc_pool_1_allocated[i] = false;
    for (uint16_t i = 0; i < 4U; i++) ppc_pool_2_allocated[i] = false;
    for (uint16_t i = 0; i < 2U; i++) ppc_pool_3_allocated[i] = false;

    /* PowerPC memory barriers for initialization */
    PPC_SYNC();
    PPC_ISYNC();
}

/* PowerPC e500 stack overflow detection */
bool ppc_check_stack_overflow(void) {
    return (ppc_main_stack.magic_start != PPC_STACK_MAGIC_START) ||
           (ppc_main_stack.magic_end != PPC_STACK_MAGIC_END);
}

/* PowerPC e500 memory pool allocation */
void* ppc_allocate_from_pool(uint16_t size) {
    for (uint16_t i = 0; i < PPC_POOL_COUNT; i++) {
        if (size <= ppc_memory_pools[i].block_size && 
            ppc_memory_pools[i].free_count > 0) {

            ppc_memory_pool_t* pool = &ppc_memory_pools[i];

            /* Find free block */
            for (uint16_t j = 0; j < pool->block_count; j++) {
                if (!pool->allocated[j]) {
                    pool->allocated[j] = true;
                    pool->free_count--;

                    /* PowerPC cache operations */
                    PPC_DCBF(0, pool->data + (j * pool->block_size));

                    return pool->data + (j * pool->block_size);
                }
            }
        }
    }

    return NULL;  /* No suitable pool */
}

/* PowerPC e500 memory pool release */
bool ppc_release_to_pool(void* ptr) {
    if (ptr == NULL) {
        return false;
    }

    /* Find which pool this pointer belongs to */
    for (uint16_t i = 0; i < PPC_POOL_COUNT; i++) {
        ppc_memory_pool_t* pool = &ppc_memory_pools[i];

        if (ptr >= pool->data && 
            ptr < pool->data + (pool->block_count * pool->block_size)) {

            /* Calculate block index */
            uint16_t block_index = ((uint8_t*)ptr - pool->data) / pool->block_size;

            if (block_index < pool->block_count && pool->allocated[block_index]) {
                pool->allocated[block_index] = false;
                pool->free_count++;

                /* PowerPC memory barriers */
                PPC_SYNC();

                return true;
            }
        }
    }

    return false;
}

/* PowerPC e500 sensor data validation with endianness handling */
bool ppc_validate_sensor_data(const ppc_sensor_data_t* sensor) {
    if (sensor == NULL) {
        return false;
    }

    /* Validate sensor ID range */
    if (sensor->sensor_id >= 32U) {
        return false;
    }

    /* Validate temperature range (-50째C to +150째C) */
    if (sensor->temperature < -500 || sensor->temperature > 1500) {
        return false;
    }

    /* Validate pressure range (800-1200 mbar) */
    if (sensor->pressure < 8000 || sensor->pressure > 12000) {
        return false;
    }

    /* Validate humidity range (0-100%) */
    if (sensor->humidity > 1000) {
        return false;
    }

    return true;
}

/* PowerPC e500 sensor data processing with bounds checking */
bool ppc_process_sensor_data(uint8_t sensor_index, const ppc_sensor_data_t* new_data) {
    if (sensor_index >= 32U || new_data == NULL) {
        return false;
    }

    if (!ppc_validate_sensor_data(new_data)) {
        return false;
    }

    /* Update sensor data with PowerPC-specific optimizations */
    ppc_sensor_system.sensors[sensor_index] = *new_data;
    ppc_sensor_system.sensors[sensor_index].timestamp = ppc_sensor_system.system_uptime;

    /* PowerPC memory barriers for data consistency */
    PPC_SYNC();

    /* Update system health based on sensor validity */
    if (new_data->is_valid) {
        ppc_sensor_system.active_count++;
    } else {
        ppc_sensor_system.active_count--;
    }

    /* Check system health threshold */
    if (ppc_sensor_system.active_count < 16U) {
        ppc_sensor_system.system_healthy = false;
    } else {
        ppc_sensor_system.system_healthy = true;
    }

    return true;
}

/* PowerPC e500 sensor array processing with bounded loops */
void ppc_process_all_sensors(void) {
    uint8_t processed_count = 0U;
    uint8_t index = 0U;

    /* Bounded loop with explicit counter - NASA Rule 2 */
    while (index < 32U && processed_count < 32U) {
        if (ppc_sensor_system.sensors[index].is_valid) {
            /* Process valid sensor data */
            ppc_sensor_data_t* sensor = &ppc_sensor_system.sensors[index];

            /* Check for critical sensor readings */
            if (sensor->temperature > 1000) {  /* > 100째C */
                /* Handle high temperature condition */
                ppc_handle_high_temperature(sensor->sensor_id, sensor->temperature);
            }

            if (sensor->pressure < 9000 || sensor->pressure > 11000) {
                /* Handle pressure anomaly */
                ppc_handle_pressure_anomaly(sensor->sensor_id, sensor->pressure);
            }

            processed_count++;
        }

        index++;
    }

    /* Update system uptime */
    ppc_sensor_system.system_uptime++;
}

/* PowerPC e500 interrupt-safe operations */
void ppc_interrupt_safe_operation(void) {
    /* Disable interrupts for critical section */
    uint32_t msr = __get_MSR();
    __disable_interrupts();

    /* Critical section - update shared data */
    ppc_sensor_system.system_uptime++;

    /* PowerPC memory barriers for interrupt safety */
    PPC_SYNC();
    PPC_ISYNC();

    /* Restore interrupt state */
    __set_MSR(msr);
}

/* PowerPC e500 cache operations */
void ppc_cache_operations(void) {
    /* Clean cache lines for sensor system */
    for (uint32_t addr = (uint32_t)&ppc_sensor_system; 
         addr < (uint32_t)&ppc_sensor_system + sizeof(ppc_sensor_system_t); 
         addr += PPC_CACHE_LINE_SIZE) {

        /* PowerPC e500 cache clean operation */
        PPC_DCBF(0, (void*)addr);
    }

    /* Synchronize cache operations */
    PPC_SYNC();

    /* Instruction synchronization barrier */
    PPC_ISYNC();
}

/* PowerPC e500 TLB operations */
void ppc_tlb_operations(void) {
    /* Invalidate TLB entries for sensor data area */
    uint32_t start_addr = (uint32_t)&ppc_sensor_system;
    uint32_t end_addr = start_addr + sizeof(ppc_sensor_system_t);

    for (uint32_t addr = start_addr; addr < end_addr; addr += 4096U) {
        /* PowerPC e500 TLB invalidate entry */
        PPC_TLBIE(addr);
    }

    /* TLB synchronization */
    PPC_TLBSYNC();
}

/* PowerPC e500 system health monitoring */
bool ppc_check_system_health(void) {
    /* Check stack overflow */
    if (ppc_check_stack_overflow()) {
        return false;
    }

    /* Check sensor system health */
    if (!ppc_sensor_system.system_healthy) {
        return false;
    }

    /* Check memory pool health */
    for (uint16_t i = 0; i < PPC_POOL_COUNT; i++) {
        if (ppc_memory_pools[i].free_count == 0) {
            return false;  /* All pools exhausted */
        }
    }

    return true;
}

/* PowerPC e500 main function with NASA compliance */
int main(void) {
    /* Initialize PowerPC e500 system */
    ppc_initialize_system();

    /* Check initial system health */
    if (!ppc_check_system_health()) {
        return -1;  /* System initialization failed */
    }

    /* Main system loop with bounded execution */
    uint32_t loop_count = 0U;
    const uint32_t MAX_LOOPS = 1000000U;  /* NASA Rule 2: Fixed upper bound */

    while (loop_count < MAX_LOOPS) {
        /* Process all sensors */
        ppc_process_all_sensors();

        /* Check system health periodically */
        if (loop_count % 1000U == 0) {
            if (!ppc_check_system_health()) {
                break;  /* Exit on health failure */
            }
        }

        /* Interrupt-safe operations */
        ppc_interrupt_safe_operation();

        /* Cache operations */
        ppc_cache_operations();

        /* TLB operations */
        ppc_tlb_operations();

        loop_count++;
    }

    return (int)loop_count;
}
