#include <stdint.h>
#include <stdbool.h>
#include <string.h>

/* x86-64 specific optimizations and NASA compliance */
#define X86_64_STACK_ALIGN 16U
#define X86_64_STACK_SIZE 16384U
#define X86_64_CACHE_LINE_SIZE 64U

/* x86-64 specific memory barriers */
#define X86_64_MFENCE() __asm volatile("mfence" : : : "memory")
#define X86_64_SFENCE() __asm volatile("sfence" : : : "memory")
#define X86_64_LFENCE() __asm volatile("lfence" : : : "memory")

/* x86-64 specific registers structure */
typedef struct {
    uint64_t rax;          /* 64-bit general purpose register A */
    uint64_t rbx;          /* 64-bit general purpose register B */
    uint64_t rcx;          /* 64-bit general purpose register C */
    uint64_t rdx;          /* 64-bit general purpose register D */
    uint64_t rsi;          /* 64-bit source index register */
    uint64_t rdi;          /* 64-bit destination index register */
    uint64_t rbp;          /* 64-bit base pointer register */
    uint64_t rsp;          /* 64-bit stack pointer register */
    uint64_t r8;           /* Additional general purpose registers */
    uint64_t r9;
    uint64_t r10;
    uint64_t r11;
    uint64_t r12;
    uint64_t r13;
    uint64_t r14;
    uint64_t r15;
} x86_64_registers_t;

/* x86-64 control registers structure */
typedef struct {
    uint64_t cr0;          /* Control Register 0 */
    uint64_t cr1;          /* Control Register 1 (reserved) */
    uint64_t cr2;          /* Control Register 2 (page fault linear address) */
    uint64_t cr3;          /* Control Register 3 (page directory base) */
    uint64_t cr4;          /* Control Register 4 */
    uint64_t cr8;          /* Control Register 8 (task priority) */
} x86_64_control_registers_t;

/* x86-64 model specific registers structure */
typedef struct {
    uint64_t efer;         /* Extended Feature Enable Register */
    uint64_t star;         /* SYSCALL Target Address Register */
    uint64_t lstar;        /* Long Mode SYSCALL Target Address Register */
    uint64_t cstar;        /* Compatibility Mode SYSCALL Target Address Register */
    uint64_t sfmask;       /* SYSCALL Flag Mask Register */
} x86_64_msr_t;

/* x86-64 sensor data structure with little-endian consideration */
typedef struct {
    uint16_t sensor_id;    /* Little-endian format */
    uint64_t timestamp;    /* x86-64 TSC timestamp */
    int32_t temperature;   /* Temperature in 0.01째C units */
    uint32_t pressure;     /* Pressure in 0.01 mbar units */
    uint16_t humidity;     /* Humidity in 0.01% units */
    bool is_valid;
    uint8_t padding[5];    /* x86-64 alignment padding */
} x86_64_sensor_data_t __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));

/* x86-64 sensor array with bounds checking */
typedef struct {
    x86_64_sensor_data_t sensors[64U];  /* Fixed size array - NASA Rule 3 */
    uint16_t active_count;
    uint64_t system_uptime;
    bool system_healthy;
    uint8_t padding[5];    /* x86-64 alignment padding */
} x86_64_sensor_system_t __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));

/* x86-64 stack with overflow protection */
typedef struct {
    uint8_t data[X86_64_STACK_SIZE];
    uint64_t magic_start;  /* Magic number for overflow detection */
    uint64_t magic_end;    /* Magic number for overflow detection */
} x86_64_stack_t;

/* Magic numbers for x86-64 stack overflow detection */
#define X86_64_STACK_MAGIC_START 0xDEADBEEFCAFEBABEULL
#define X86_64_STACK_MAGIC_END 0xCAFEBABEDEADBEEFULL

/* x86-64 static memory pools with cache line alignment */
#define X86_64_POOL_COUNT 5U
#define X86_64_POOL_0_SIZE 64U
#define X86_64_POOL_1_SIZE 128U
#define X86_64_POOL_2_SIZE 256U
#define X86_64_POOL_3_SIZE 512U
#define X86_64_POOL_4_SIZE 1024U

/* x86-64 memory pool structure */
typedef struct {
    uint8_t* data;
    uint32_t block_size;
    uint32_t block_count;
    bool* allocated;
    uint32_t free_count;
    uint8_t padding[4];    /* x86-64 alignment padding */
} x86_64_memory_pool_t;

/* x86-64 static memory pool data with cache line alignment */
static uint8_t x86_64_pool_0_data[X86_64_POOL_0_SIZE * 32U] __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));
static uint8_t x86_64_pool_1_data[X86_64_POOL_1_SIZE * 16U] __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));
static uint8_t x86_64_pool_2_data[X86_64_POOL_2_SIZE * 8U] __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));
static uint8_t x86_64_pool_3_data[X86_64_POOL_3_SIZE * 4U] __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));
static uint8_t x86_64_pool_4_data[X86_64_POOL_4_SIZE * 2U] __attribute__((aligned(X86_64_CACHE_LINE_SIZE)));

static bool x86_64_pool_0_allocated[32U];
static bool x86_64_pool_1_allocated[16U];
static bool x86_64_pool_2_allocated[8U];
static bool x86_64_pool_3_allocated[4U];
static bool x86_64_pool_4_allocated[2U];

static x86_64_memory_pool_t x86_64_memory_pools[X86_64_POOL_COUNT];

/* x86-64 system instances */
static x86_64_sensor_system_t x86_64_sensor_system;
static x86_64_stack_t x86_64_main_stack __attribute__((aligned(X86_64_STACK_ALIGN)));

/* x86-64 endianness handling for little-endian systems */
static inline uint16_t x86_64_read_16_le(const uint8_t* data) {
    return (uint16_t)(data[0] | (data[1] << 8));
}

static inline uint32_t x86_64_read_32_le(const uint8_t* data) {
    return (uint32_t)(data[0] | (data[1] << 8) | (data[2] << 16) | (data[3] << 24));
}

static inline uint64_t x86_64_read_64_le(const uint8_t* data) {
    return (uint64_t)(data[0] | (data[1] << 8) | (data[2] << 16) | (data[3] << 24) |
                      ((uint64_t)data[4] << 32) | ((uint64_t)data[5] << 40) |
                      ((uint64_t)data[6] << 48) | ((uint64_t)data[7] << 56));
}

static inline void x86_64_write_16_le(uint8_t* data, uint16_t value) {
    data[0] = (uint8_t)(value & 0xFF);
    data[1] = (uint8_t)(value >> 8);
}

static inline void x86_64_write_32_le(uint8_t* data, uint32_t value) {
    data[0] = (uint8_t)(value & 0xFF);
    data[1] = (uint8_t)((value >> 8) & 0xFF);
    data[2] = (uint8_t)((value >> 16) & 0xFF);
    data[3] = (uint8_t)((value >> 24) & 0xFF);
}

static inline void x86_64_write_64_le(uint8_t* data, uint64_t value) {
    data[0] = (uint8_t)(value & 0xFF);
    data[1] = (uint8_t)((value >> 8) & 0xFF);
    data[2] = (uint8_t)((value >> 16) & 0xFF);
    data[3] = (uint8_t)((value >> 24) & 0xFF);
    data[4] = (uint8_t)((value >> 32) & 0xFF);
    data[5] = (uint8_t)((value >> 40) & 0xFF);
    data[6] = (uint8_t)((value >> 48) & 0xFF);
    data[7] = (uint8_t)((value >> 56) & 0xFF);
}

/* x86-64 specific initialization */
void x86_64_initialize_system(void) {
    /* Initialize x86-64 stack with overflow protection */
    x86_64_main_stack.magic_start = X86_64_STACK_MAGIC_START;
    x86_64_main_stack.magic_end = X86_64_STACK_MAGIC_END;

    /* Initialize x86-64 sensor system */
    memset(&x86_64_sensor_system, 0, sizeof(x86_64_sensor_system_t));
    x86_64_sensor_system.system_healthy = true;

    /* Initialize x86-64 memory pools */
    x86_64_memory_pools[0].data = x86_64_pool_0_data;
    x86_64_memory_pools[0].block_size = X86_64_POOL_0_SIZE;
    x86_64_memory_pools[0].block_count = 32U;
    x86_64_memory_pools[0].allocated = x86_64_pool_0_allocated;
    x86_64_memory_pools[0].free_count = 32U;

    x86_64_memory_pools[1].data = x86_64_pool_1_data;
    x86_64_memory_pools[1].block_size = X86_64_POOL_1_SIZE;
    x86_64_memory_pools[1].block_count = 16U;
    x86_64_memory_pools[1].allocated = x86_64_pool_1_allocated;
    x86_64_memory_pools[1].free_count = 16U;

    x86_64_memory_pools[2].data = x86_64_pool_2_data;
    x86_64_memory_pools[2].block_size = X86_64_POOL_2_SIZE;
    x86_64_memory_pools[2].block_count = 8U;
    x86_64_memory_pools[2].allocated = x86_64_pool_2_allocated;
    x86_64_memory_pools[2].free_count = 8U;

    x86_64_memory_pools[3].data = x86_64_pool_3_data;
    x86_64_memory_pools[3].block_size = X86_64_POOL_3_SIZE;
    x86_64_memory_pools[3].block_count = 4U;
    x86_64_memory_pools[3].allocated = x86_64_pool_3_allocated;
    x86_64_memory_pools[3].free_count = 4U;

    x86_64_memory_pools[4].data = x86_64_pool_4_data;
    x86_64_memory_pools[4].block_size = X86_64_POOL_4_SIZE;
    x86_64_memory_pools[4].block_count = 2U;
    x86_64_memory_pools[4].allocated = x86_64_pool_4_allocated;
    x86_64_memory_pools[4].free_count = 2U;

    /* Initialize allocation flags */
    for (uint32_t i = 0; i < 32U; i++) x86_64_pool_0_allocated[i] = false;
    for (uint32_t i = 0; i < 16U; i++) x86_64_pool_1_allocated[i] = false;
    for (uint32_t i = 0; i < 8U; i++) x86_64_pool_2_allocated[i] = false;
    for (uint32_t i = 0; i < 4U; i++) x86_64_pool_3_allocated[i] = false;
    for (uint32_t i = 0; i < 2U; i++) x86_64_pool_4_allocated[i] = false;

    /* x86-64 memory barriers for initialization */
    X86_64_MFENCE();
    X86_64_SFENCE();
    X86_64_LFENCE();
}

/* x86-64 stack overflow detection */
bool x86_64_check_stack_overflow(void) {
    return (x86_64_main_stack.magic_start != X86_64_STACK_MAGIC_START) ||
           (x86_64_main_stack.magic_end != X86_64_STACK_MAGIC_END);
}

/* x86-64 memory pool allocation */
void* x86_64_allocate_from_pool(uint32_t size) {
    for (uint32_t i = 0; i < X86_64_POOL_COUNT; i++) {
        if (size <= x86_64_memory_pools[i].block_size && 
            x86_64_memory_pools[i].free_count > 0) {

            x86_64_memory_pool_t* pool = &x86_64_memory_pools[i];

            /* Find free block */
            for (uint32_t j = 0; j < pool->block_count; j++) {
                if (!pool->allocated[j]) {
                    pool->allocated[j] = true;
                    pool->free_count--;

                    /* x86-64 cache operations */
                    void* block_ptr = pool->data + (j * pool->block_size);
                    __asm volatile("clflush [%0]" : : "r"(block_ptr) : "memory");

                    return block_ptr;
                }
            }
        }
    }

    return NULL;  /* No suitable pool */
}

/* x86-64 memory pool release */
bool x86_64_release_to_pool(void* ptr) {
    if (ptr == NULL) {
        return false;
    }

    /* Find which pool this pointer belongs to */
    for (uint32_t i = 0; i < X86_64_POOL_COUNT; i++) {
        x86_64_memory_pool_t* pool = &x86_64_memory_pools[i];

        if (ptr >= pool->data && 
            ptr < pool->data + (pool->block_count * pool->block_size)) {

            /* Calculate block index */
            uint32_t block_index = ((uint8_t*)ptr - pool->data) / pool->block_size;

            if (block_index < pool->block_count && pool->allocated[block_index]) {
                pool->allocated[block_index] = false;
                pool->free_count++;

                /* x86-64 memory barriers */
                X86_64_MFENCE();

                return true;
            }
        }
    }

    return false;
}

/* x86-64 sensor data validation with endianness handling */
bool x86_64_validate_sensor_data(const x86_64_sensor_data_t* sensor) {
    if (sensor == NULL) {
        return false;
    }

    /* Validate sensor ID range */
    if (sensor->sensor_id >= 64U) {
        return false;
    }

    /* Validate temperature range (-50째C to +150째C) */
    if (sensor->temperature < -5000 || sensor->temperature > 15000) {
        return false;
    }

    /* Validate pressure range (800-1200 mbar) */
    if (sensor->pressure < 80000 || sensor->pressure > 120000) {
        return false;
    }

    /* Validate humidity range (0-100%) */
    if (sensor->humidity > 10000) {
        return false;
    }

    return true;
}

/* x86-64 sensor data processing with bounds checking */
bool x86_64_process_sensor_data(uint16_t sensor_index, const x86_64_sensor_data_t* new_data) {
    if (sensor_index >= 64U || new_data == NULL) {
        return false;
    }

    if (!x86_64_validate_sensor_data(new_data)) {
        return false;
    }

    /* Update sensor data with x86-64-specific optimizations */
    x86_64_sensor_system.sensors[sensor_index] = *new_data;
    x86_64_sensor_system.sensors[sensor_index].timestamp = x86_64_sensor_system.system_uptime;

    /* x86-64 memory barriers for data consistency */
    X86_64_MFENCE();

    /* Update system health based on sensor validity */
    if (new_data->is_valid) {
        x86_64_sensor_system.active_count++;
    } else {
        x86_64_sensor_system.active_count--;
    }

    /* Check system health threshold */
    if (x86_64_sensor_system.active_count < 32U) {
        x86_64_sensor_system.system_healthy = false;
    } else {
        x86_64_sensor_system.system_healthy = true;
    }

    return true;
}

/* x86-64 sensor array processing with bounded loops */
void x86_64_process_all_sensors(void) {
    uint16_t processed_count = 0U;
    uint16_t index = 0U;

    /* Bounded loop with explicit counter - NASA Rule 2 */
    while (index < 64U && processed_count < 64U) {
        if (x86_64_sensor_system.sensors[index].is_valid) {
            /* Process valid sensor data */
            x86_64_sensor_data_t* sensor = &x86_64_sensor_system.sensors[index];

            /* Check for critical sensor readings */
            if (sensor->temperature > 10000) {  /* > 100째C */
                /* Handle high temperature condition */
                x86_64_handle_high_temperature(sensor->sensor_id, sensor->temperature);
            }

            if (sensor->pressure < 90000 || sensor->pressure > 110000) {
                /* Handle pressure anomaly */
                x86_64_handle_pressure_anomaly(sensor->sensor_id, sensor->pressure);
            }

            processed_count++;
        }

        index++;
    }

    /* Update system uptime */
    x86_64_sensor_system.system_uptime++;
}

/* x86-64 interrupt-safe operations */
void x86_64_interrupt_safe_operation(void) {
    /* Disable interrupts for critical section */
    uint64_t rflags = __get_RFLAGS();
    __disable_interrupts();

    /* Critical section - update shared data */
    x86_64_sensor_system.system_uptime++;

    /* x86-64 memory barriers for interrupt safety */
    X86_64_MFENCE();
    X86_64_SFENCE();
    X86_64_LFENCE();

    /* Restore interrupt state */
    __set_RFLAGS(rflags);
}

/* x86-64 cache operations */
void x86_64_cache_operations(void) {
    /* Clean cache lines for sensor system */
    for (uint64_t addr = (uint64_t)&x86_64_sensor_system; 
         addr < (uint64_t)&x86_64_sensor_system + sizeof(x86_64_sensor_system_t); 
         addr += X86_64_CACHE_LINE_SIZE) {

        /* x86-64 cache clean operation */
        __asm volatile("clflush [%0]" : : "r"(addr) : "memory");
    }

    /* Synchronize cache operations */
    X86_64_MFENCE();

    /* Instruction synchronization barrier */
    X86_64_LFENCE();
}

/* x86-64 prefetch operations */
void x86_64_prefetch_operations(void) {
    /* Prefetch next sensor data for better cache performance */
    for (uint16_t i = 0; i < 64U; i += 4U) {
        if (i < 64U) {
            /* Prefetch next cache line */
            __asm volatile("prefetchnta [%0]" : : "r"(&x86_64_sensor_system.sensors[i]) : "memory");
        }
    }

    /* Memory fence after prefetch operations */
    X86_64_SFENCE();
}

/* x86-64 system health monitoring */
bool x86_64_check_system_health(void) {
    /* Check stack overflow */
    if (x86_64_check_stack_overflow()) {
        return false;
    }

    /* Check sensor system health */
    if (!x86_64_sensor_system.system_healthy) {
        return false;
    }

    /* Check memory pool health */
    for (uint32_t i = 0; i < X86_64_POOL_COUNT; i++) {
        if (x86_64_memory_pools[i].free_count == 0) {
            return false;  /* All pools exhausted */
        }
    }

    return true;
}

/* x86-64 main function with NASA compliance */
int main(void) {
    /* Initialize x86-64 system */
    x86_64_initialize_system();

    /* Check initial system health */
    if (!x86_64_check_system_health()) {
        return -1;  /* System initialization failed */
    }

    /* Main system loop with bounded execution */
    uint64_t loop_count = 0U;
    const uint64_t MAX_LOOPS = 1000000ULL;  /* NASA Rule 2: Fixed upper bound */

    while (loop_count < MAX_LOOPS) {
        /* Process all sensors */
        x86_64_process_all_sensors();

        /* Check system health periodically */
        if (loop_count % 1000ULL == 0) {
            if (!x86_64_check_system_health()) {
                break;  /* Exit on health failure */
            }
        }

        /* Interrupt-safe operations */
        x86_64_interrupt_safe_operation();

        /* Cache operations */
        x86_64_cache_operations();

        /* Prefetch operations */
        x86_64_prefetch_operations();

        loop_count++;
    }

    return (int)loop_count;
}
